mapper.select:
SELECT phone, COUNT(*) AS count
FROM jc_orders
WHERE create_time >= #{startTime}
AND phone IN

    #{phone}

GROUP BY phone

SELECT sum(sale) AS total_sale
FROM jc_orders
WHERE create_time <= #{start}
AND create_time >= #{end}
AND is_after_sale = #{type}

SELECT sum(after_sale_amount) AS total_after_sale
FROM jc_orders
WHERE create_time <= #{start}
AND create_time >= #{end}
AND is_after_sale = 1

select sum(sale) as all_sale,sum(cost) all_cost,count(*) as all_count
where create_time <= #{start}
        AND create_time >= #{end}
        AND is_after_sale = 0
